(function(){$(function(){return $(".plus").click(function(){var i,n;return n=$(this).siblings(".line_item_quantity").children(".quantity"),i=parseInt(n.val(),10),n.val(1>i?1:i+1)}),$(".minus").click(function(){var i,n;return n=$(this).siblings(".line_item_quantity").children(".quantity"),i=parseInt(n.val(),10),n.val(2>=i?1:i-1)}),$("input").blur(function(){return $(this).closest("form").submit()})})}).call(this);